package questao3;

public class testeAlgo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int INDICE = 12;
		int SOMA = 0;
		int K = 1;
		
		while( K < INDICE)
		{ 
			K = K + 1;
			SOMA = SOMA + K;
		}
		
		System.out.println(SOMA);
	}

}
